func FindBasement( s string)int{
	sum:=0
	var index int
	for i:=0;i<len(s);i++{
		
		if s[i]=='('{
			sum++
		}
		if s[i]==')'{
			sum--
		}

		if sum==-1{
          index=i
		  break
		}
	}
	return index +1
}